useEffect(() => {
        const getMovies = async () => {
            const peliculesRef = collection(db, 'pelicules');
            const q = query(peliculesRef);
            try {
                const querySnapshot = await getDocs(q);
                const movies = [];
                querySnapshot.forEach((doc) => {
                    movies.push({
                        id: doc.id,
                        ...doc.data()
                    });
                });
                setMovies(movies);
            } catch (error) {
                console.error('Error:', error);
            }
        }
        getMovies();
    }, []);

